<?php$model = Sales::model()->findAllByAttributes(array('status' => 0));$data = CHtml::listData($model, 'table', 'id');for ($a = 0; $a < 50; $a++) {    if (isset($data[$a]))        echo '<input onClick="hold_bill(' . $a . ',1,' . $data[$a] . ')" type="button" value="' . $a . ' Terisi" data-dropdown="#dropdown-4" class="color-orange color" />';    else        echo '<input onClick="hold_bill(' . $a . ',0,0)" type="button" value="' . $a . '" data-dropdown="#dropdown-4" class="color-orange color" />';}?>